svg.script:
function updateSVG() {
    // Simulating API response with hardcoded data
    const data = {
        avatar_url: 'https://avatars.githubusercontent.com/u/127134568?v=4',
        login: 'manuemmanuel',
        followers: 27,
        public_repos: 17
    };

    const profileImage = document.getElementById('profile-image');
    profileImage.setAttribute('href', data.avatar_url);
    profileImage.setAttribute('rx', '30'); // Add border radius
    profileImage.setAttribute('ry', '30');
    profileImage.classList.remove('loading-image'); // Remove loading animation class

    document.getElementById('username').textContent = data.login;
    document.getElementById('followers').textContent = 'Followers: ' + data.followers;
    document.getElementById('repositories').textContent = 'Repositories: ' + data.public_repos;

    // Remove loading animation class from text elements
    document.getElementById('username').classList.remove('loading-text');
    document.getElementById('followers').classList.remove('loading-text');
    document.getElementById('repositories').classList.remove('loading-text');
}

// Call the function initially
updateSVG();

// Set up a timer to update the SVG content periodically (every 15 minutes in this example)
setInterval(updateSVG, 900000); // 15 minutes = 900,000 milliseconds
