svg.script:
// Fetch GitHub data using the GitHub API
const apiUrl = 'https://api.github.com/users/';
const username = 'manuemmanuel';

fetch(apiUrl + username)
    .then(response => response.json())
    .then(data => {
        // Update profile information
        document.getElementById('profile-image').setAttribute('href', data.avatar_url);
        document.getElementById('username').textContent = data.login;
        document.getElementById('bio').textContent = data.bio;
        document.getElementById('profile-link').setAttribute('href', data.html_url);

        // Fetch repository data
        return fetch(apiUrl + username + '/repos');
    })
    .then(response => response.json())
    .then(data => {
        // Update repository information (using the first repository as an example)
        const firstRepo = data[0];
        document.getElementById('repo-title').textContent = firstRepo.name;
        document.getElementById('repo-description').textContent = firstRepo.description;
        document.getElementById('repo-link').setAttribute('href', firstRepo.html_url);
    })
    .catch(error => console.error('Error fetching GitHub data:', error));
